#!/bin/bash
mkdir -p /var/containers/wazuh/
chcon -R system_u:object_r:admin_home_t:s0 /var/containers/wazuh/
echo "Contextualización de SELinux"

sysctl -w vm.max_map_count=262144
echo "Memoria virtual modificada"

#Creación y configuración de directorios para elasticsearch
mkdir -p /var/containers/wazuh/elk/elasticsearch/
echo "" | base64 -w0 -d > /var/containers/wazuh/elk/elasticsearch/elasticsearch.yml
docker run --name=elasticsearch_wazuh_1 -p 9200:9200 -p 9300:9300 -d -e "discovery.type=single-node" -v /var/containers/wazuh/elk/elasticsearch/elasticsearch.yml:/usr/share/elasticsearch/config/elasticsearch.yml:z docker.elastic.co/elasticsearch/elasticsearch:6.4.2
echo "Elasticsearch creado"

#Creación y configuración de directorios para logstash
mkdir -p /var/containers/wazuh/logstash/pipeline/
echo "" | base64 -w0 -d > /var/containers/wazuh/logstash/pipeline/pipeline.conf
#Creación de contenedor de logstash
docker run --rm -d --name=logstash_wazuh_1 --link=elasticsearch_wazuh_1:elasticsearch -v /var/containers/wazuh/logstash/pipeline/:/usr/share/logstash/pipeline/bin:z docker.elastic.co/logstash/logstash-oss:6.2.1
echo "Logstash creado"

#Creación y configuración de directorios para wazuh
mkdir -p /var/containers/wazuh/wazuh/wazuh-config-mount/etc/
mkdir -p /var/containers/wazuh/wazuh/etc/filebeat/
echo "" | base64 -w0 -d > /var/containers/wazuh/wazuh/etc/filebeat/filebeat.yml
echo "" | base64 -w0 -d > /var/containers/wazuh/wazuh/wazuh-config-mount/etc/ossec.conf
docker run --name=wazuh_1 -d --link=logstash_wazuh_1:logstash -p 1514:1514/udp -p 1515:1515 -p 514:514/udp -p 55000:55000 -v /var/containers/wazuh/wazuh/var/ossec/data:/var/ossec/data:z -v /var/containers/wazuh/wazuh/etc/postfix:/etc/postfix:z -v /var/containers/wazuh/wazuh/etc/filebeat:/etc/filebeat:z -v /var/containers/wazuh/wazuh/wazuh-config-mount/etc/ossec.conf:/wazuh-config-mount/etc/ossec.conf -v /var/containers/wazuh/wazuh/etc/filebeat/filebeat.yml:/etc/filebeat/filebeat.yml:z wazuh/wazuh:3.6.1_6.4.2
echo "Wazuh creado"

#Creación y configuración de directorios para kibana
mkdir -p /var/containers/wazuh/elk/kibana/
echo "" | base64 -w0 -d > /var/containers/wazuh/elk/kibana/kibana.yml
docker run --name=kibana_wazuh_1 --link=elasticsearch_wazuh_1:elasticsearch --link=logstash_wazuh_1:logstash -p 5601:5601 -d -v /var/containers/wazuh/elk/kibana/kibana.yml:/usr/share/kibana/config/kibana.yml:z wazuh/wazuh-kibana:3.6.1_6.4.2
echo "Kibana creado"

echo "Consulta la ruta http://localhost:5601"
